import React, {Component} from 'react'
import Checkbox from 'material-ui/Checkbox'

class FilterCheckboxes extends Component{
	render(){
		return(
			<div className="b-filter-checkboxes">
				<Checkbox
          label="Apple"
          onClick={this.props.onChangeCheckboxes.bind(null, '1')}
        />
        <Checkbox
          label="Samsung"
          onClick={this.props.onChangeCheckboxes.bind(null, '2')}
        />
        <Checkbox
          label="Lenovo"
          onClick={this.props.onChangeCheckboxes.bind(null, '4')}
        />
        <Checkbox
          label="Nokia"
          onClick={this.props.onChangeCheckboxes.bind(null, '5')}
        />
        <Checkbox
          label="HTC"
          onClick={this.props.onChangeCheckboxes.bind(null, '3')}
        />
			</div>
		)
	}
}

export default FilterCheckboxes