import React, {Component} from 'react'
import PropTypes from 'prop-types'
import RaisedButton from 'material-ui/RaisedButton'

import './style.css'

class ProductListItem extends Component{
	render(){

		const {id, name, link, srcImg} = this.props
		const style = {
			margin: 12,
		};

		return(
			<div className="product-list-item">
				<a href={`#$(id)`} className="product-list-item__img">
					<img src={srcImg} />
				</a>
				<a href={link} className="product-list-item__name">{name}</a>
				<RaisedButton className="product-list-item__btn" label="Primary" primary={true} />
			</div>
		)
	}
}

ProductListItem.propTypes = {
	product: PropTypes.shape({
		id: PropTypes.number.isRequired,
		name: PropTypes.string.isRequired,
		link: PropTypes.string.isRequired,
		srcImg: PropTypes.string.isRequired
	})
}

export default ProductListItem