import {SEARCH, FILTER_BRANDS} from 'constans'

const initialState = {
	search: '',
	brands: {}
}

export default (state = initialState, action) => {
	const {type, payload} = action

	switch(type){
		case SEARCH:
			return {...state, search: payload}
		case FILTER_BRANDS:
		return {
			...state,
			brands: {
				...state.brands, [payload]: state.brands[payload] == undefined ? true : !state.brands[payload]
			}
		}
		default:
			return state
	}
}