import {LOAD_PRODUCTS, START, SUCCESS} from 'constans'

const initialState = {
	loaded: false,
	loading: false,
	products: []
}

function loadProducts(state = initialState, action){
	const {type, payload} = action;
	switch(type){
		case LOAD_PRODUCTS+START:
			return {...state, loading: true}
		case LOAD_PRODUCTS+SUCCESS:
			return {...state, loaded: true, loading: false}
		default:
			return state;
	}
}

export default loadProducts;