import {
	START,
	SUCCESS,
	//FAILURE,
	LOAD_PRODUCT
} from 'constans.js'

const initialState = {}

export default (state = initialState, action) => {
	const {type, payload} = action
	switch( type ){
		case LOAD_PRODUCT+START:
			return {
				...state,
				[payload.id]: {loading: true}
			}
		case LOAD_PRODUCT+SUCCESS:
			return {
				...state,
				[payload.id]: {loading: false, loaded: true, prop: payload.res}
			}
		default:
			return state
	}
}