import {
	START,
	SUCCESS,
	FAILURE,
	LOAD_PRODUCT
} from 'constans.js'

const initialState = {}

export default (state = initialState, payload) => {
	const {type} = payload
	switch( type ){
		case LOAD_PRODUCT+SUCCESS:
			console.log('load-product', payload)
			return payload
		default:
			return state
	}
}